import React, { Fragment, useState, useEffect } from "react";
import "h8k-components";

import { image1, image2, image3, image4 } from "./assets/images";
import { Thumbs, Viewer } from "./components";

const title = "Catalog Viewer";

function App() {
  const catalogsList = [
    {
      thumb: image1,
      image: image1,
    },
    {
      thumb: image2,
      image: image2,
    },
    {
      thumb: image3,
      image: image3,
    },
    {
      thumb: image4,
      image: image4,
    },
  ];

  const [catalogs] = useState([...catalogsList]);
  const [activeIndex, setActiveIndex] = useState(0);
  const [slideTimer, setSlideTimer] = useState(false);
  const [slideDuration] = useState(3000);

  async function getRicAndMortyCharacters() {
    const url = "https://rickandmortyapi.com/api/character";
    try {
      const res = await fetch(url);
      const { results } = await res.json();
      console.log(res);
      const slicedResults = results.slice(0, 5);
      const mappedResults = slicedResults.map((caracter) => {
        return {
          nome: caracter.name,
          avatar: caracter.image,
          genero: caracter.gender,
          especie: caracter.species,
        };
      });
      return mappedResults;
    } catch (err) {
      console.log(err.message);
      return [];
    }
  }

  useEffect(() => {
    getRicAndMortyCharacters();
  }, []);

  return (
    <Fragment>
      <h8k-navbar header={title}></h8k-navbar>
      <div className="layout-column justify-content-center mt-75">
        {/* <button type="button" onClick={console.log(maskify("hello world"))}>
          teste função
        </button> */}
        <div className="layout-row justify-content-center">
          <div className="card pt-25">
            <Viewer catalogImage={catalogs[activeIndex].image} />
            <div className="layout-row justify-content-center align-items-center mt-20">
              <button
                className="icon-only outlined"
                data-testid="prev-slide-btn"
              >
                <i className="material-icons">arrow_back</i>
              </button>
              <Thumbs items={catalogs} currentIndex={activeIndex} />
              <button
                className="icon-only outlined"
                data-testid="next-slide-btn"
              >
                <i className="material-icons">arrow_forward</i>
              </button>
            </div>
          </div>
        </div>
        <div className="layout-row justify-content-center mt-25">
          <input type="checkbox" data-testid="toggle-slide-show-button" />
          <label className="ml-6">Start Slide Show</label>
        </div>
      </div>
    </Fragment>
  );
}

export default App;
